import React, { useState, useEffect } from "react";
import "../component/style.css";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Tempapp = () => {
  const [city, setCity] = useState(null);
  const [data, setData] = useState({});
  const [time, setTime] = useState("");
  const [search, setSearch] = useState("New York City");

  useEffect(() => {
    const fetchApi = async () => {
      // const url = `https://api.weatherapi.com/v1/current.json?key=c760b5f1c647c77fc3ca9c98881ce504&q=${search}`;
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=f387a45bf8af17921f35a4fff7101bf1`;
      const res = await fetch(url);
      const resJson = await res.json();
      console.log(resJson.name);
      setData(resJson);

      setCity(resJson.name);
      // setTemp(resJson.main);
      // setWeather(resJson.weather);
      // setTime(resJson.location);
    };

    fetchApi();
  }, [search]);

  const inputEvent = (e) => {
    setSearch(e.target.value);
  };

  return (
    <>
      <div className="album py-5">
        <div className="container">
          <h1 className="main_heading">Wheather Report</h1>
          <div className="row d-flex justify-content-between align-items-center">
            <div className="col">
              <div className="card">
                <input
                  type="search"
                  placeholder="Search Anyplace Name"
                  onChange={inputEvent}
                />
                {!city ? (
                  <p>No Data Found</p>
                ) : (
                  <div>
                    <div className="card_main">
                      <img
                        className="main_img"
                        src={`http://openweathermap.org/img/w/${data?.weather[0].icon}.png`}
                        alt={`http://openweathermap.org/img/w/${data?.weather[0].icon}.png`}
                      />
                      <h3 className="text-center">
                        {city}, {data?.sys?.country}
                      </h3>
                      {/* <h3 className="text-center">{data?.weather[0].icon}</h3> */}
                      <h4 className="text-center">{data?.main?.temp}</h4>
                      {/* <h2 className="text-center">{search}</h2> */}
                    </div>
                    {/* <div className="card-body">
                      <select className="select text-center">
                        <option value="otherOption">{city.temp_f}°F</option>
                        <option value="someOption">{city.temp_c}°C</option>
                      </select>
                      <p className="card-text"></p>
                      <div className="d-flex justify-content-between align-items-center">
                        <div className="btn-group">
                          <button type="button" className="btn">
                            Humidity: {city.humidity}%
                          </button>
                        </div>
                        <div className="btn-group">
                          <button type="button" className="btn">
                            Time: {time.localtime}
                          </button>
                        </div>
                        <div className="btn-group">
                          <select className="select text-center">
                            <option value="someOption">
                              Wind_mph: {city.wind_mph}
                            </option>
                            <option value="otherOption">
                              Wind_kph: {city.wind_kph}
                            </option>
                          </select>
                        </div>
                      </div>
                    </div> */}
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Tempapp;
