import React, { useState, useEffect } from "react";
import "../component/style.css";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Tempapp = () => {
  const [city, setCity] = useState(null);
  const [time, setTime] = useState("");
  const [search, setSearch] = useState("Arrabiata");

  useEffect(() => {
    const options = {
      method: "GET",
      headers: {
        "X-RapidAPI-Key": "5b7c09866fmsh11f209ffdbadbcep1c9875jsnb74f6e7a8857",
        "X-RapidAPI-Host": "themealdb.p.rapidapi.com",
      },
    };
    const fetchApi = async () => {
      //   const url = `https://themealdb.p.rapidapi.com/random.php?key=5b7c09866fmsh11f209ffdbadbcep1c9875jsnb74f6e7a8857&q=${search}`;
      const res = await fetch(
        "https://themealdb.p.rapidapi.com/search.php?s=Arrabiata",
        options
      )
        .then((response) => response.json())
        .then((response) => console.log(response))
        .catch((err) => console.error(err));
      const resJson = await res.json();
      //   console.log(resJson);
      setCity(resJson.current);
      setTime(resJson.location);
    };

    fetchApi();
  }, [search]);

  const inputEvent = (e) => {
    setSearch(e.target.value);
  };

  return (
    <>
      <div className="album py-5">
        <div className="container">
          <h1 className="main_heading">Wheather Report</h1>
          <div className="row d-flex justify-content-between align-items-center">
            <div className="col">
              <div className="card">
                <input
                  type="search"
                  placeholder="Search Anyplace Name"
                  onChange={inputEvent}
                />
                {!city ? (
                  <p>No Data Found</p>
                ) : (
                  <div>
                    <div className="card_main">
                      <img
                        className="main_img"
                        src={city.condition.icon}
                        alt={city.condition.icon}
                      />
                      <h3 className="text-center">{city.condition.text}</h3>
                      <h2 className="text-center">
                        {search}, {time.country}
                      </h2>
                    </div>
                    <div className="card-body">
                      <h1 className="text-center">{city.temp_c}Â°C</h1>
                      <p className="card-text"></p>
                      <div className="d-flex justify-content-between align-items-center">
                        <div className="btn-group">
                          <button type="button" className="btn">
                            Humidity: {city.humidity}
                          </button>
                        </div>
                        <div className="btn-group">
                          <button type="button" className="btn">
                            Time: {time.localtime}
                          </button>
                        </div>
                        <div className="btn-group">
                          <button type="button" className="btn">
                            Wind_kph: {city.wind_kph}
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Tempapp;
