import React, { useState, useEffect } from "react";
import "../component/style.css";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Tempapp = () => {
  const [city, setCity] = useState(null);
  const [search, setSearch] = useState("Jamshedpur");

  useEffect(() => {
    const fetchApi = async () => {
      const url = `http://api.weatherapi.com/v1/current.json?key=41533b7315214268b54104730232801&q=${search}`;
      const res = await fetch(url);
      const resJson = await res.json();
      //   console.log(resJson);
      setCity(resJson);
    };

    fetchApi();
  }, [search]);

  const inputEvent = (e) => {
    setSearch(e.target.value);
  };

  return (
    <>
      <div className="album py-5">
        <div className="container">
          <div className="row d-flex justify-content-between align-items-center">
            <div className="col">
              <div className="card shadow-sm">
                <input type="search" onChange={inputEvent} />
                {!city ? (
                  <p>No Data Found</p>
                ) : (
                  <div>
                    <div className="card_main">
                      <img
                        className="main_img"
                        src={city.condition.icon}
                        alt={city.condition.icon}
                      />
                      <h3 className="text-center">{city.condition.text}</h3>
                      <h2 className="text-center">{search}</h2>
                    </div>
                    <div className="card-body">
                      <h1 className="text-center">{city.temp_c}°C</h1>
                      <p className="card-text"></p>
                      <div className="d-flex justify-content-between align-items-center">
                        <div className="btn-group">
                          <button type="button" className="btn">
                            Min Temp: -5.35°Cel
                          </button>
                        </div>
                        <div className="btn-group">
                          <button type="button" className="btn">
                            Max Temp: 5.35°Cel
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Tempapp;
